--  DDL for Procedure CALCULATE_LENDING
--------------------------------------------------------
set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "IT011873"."CALCULATE_LENDING" (
    plend_date  IN VARCHAR2,
    pbook_id    IN NUMBER,
    returnprice OUT NUMBER,
    rentdays    OUT NUMBER,
    cat         OUT NUMBER,
    price       OUT NUMBER,
    overprice   OUT NUMBER
) AS
BEGIN
-- FIND THE DAYS
    SELECT
        round(((
            SELECT
                sysdate
            FROM
                dual
        ) - TO_DATE(plend_date, 'DD/MM/YYYY')),
              0)
    INTO rentdays
    FROM
        dual;
    SELECT
        lc.lend_code,
        lc.price,
        lc.overdue_price
    INTO
        cat,
        price,
        overprice
    FROM
        books           b,
        lend_categories lc
    WHERE
            b.id = pbook_id
        AND b.lend_category = lc.id;

    IF ( cat = 1 ) THEN
        IF (rentdays <= 3) THEN
        returnprice := rentdays * price;
        
        ELSE 
        returnprice := 3 * price;
        rentdays := rentdays - 3;
            returnprice := returnprice + (rentdays * overprice);
